import Vue from 'vue'
import Router from 'vue-router'
import Home from '../pages/Home.vue'
import Detail from '../pages/Detail.vue'

Vue.use(Router)
export function createRouter () {
  return new Router({
    mode: 'history',
    routes: [
      {
        path: '/',
        component: Home
      },
      {
        path: '/news/:id',
        component: Detail
      }
    ]
  })
}

import axios from 'axios'
const baseURL = 'https://news-at.zhihu.com/api/4/news/'

export function getNewsList() {
  return axios.get(baseURL + 'latest')
}
export function getNewsContent(id) {
  return axios.get(baseURL + id)
}

import Vue from 'vue'
import Vuex from 'vuex'
import { getNewsList, getNewsContent } from '../api'

Vue.use(Vuex)

export function createStore () {
  return new Vuex.Store({
    state: {
      newsList: [],
      newsContent: {}
    },
    actions: {
      getNewsList ({ commit }) {
        return getNewsList().then(res => {
          const newsList = res.data.stories
          commit('setNewsList', { newsList })
        })
      },
      getNewsContent ({commit}, id) {
        return getNewsContent(id).then(res => {
          commit('setNewsContent', { newsContent: res.data })
        })
      }
    },
    mutations: {
      setNewsList (state, { newsList }) {
        Vue.set(state, 'newsList', newsList)
      },
      setNewsContent (state, { newsContent }) {
        Vue.set(state, 'newsContent', newsContent)
      }
    }
  })
}

import Vue from 'vue'
import App from './App.vue'
import { sync } from 'vuex-router-sync'
import { createRouter } from './router'
import { createStore  } from './store'

export function createApp () {
  const router = createRouter()
  const store = createStore()
  sync(store, router)
  const app = new Vue({
    router,
    store,
    render: h => h(App)
  })
  return { app, router, store }
}

import { createApp } from './app'

export default context => {
  return new Promise((resolve, reject) => {
    const { app, router, store } = createApp()
    router.push(context.url)
    router.onReady(() => {
      const matchedComponents = router.getMatchedComponents()
      if (!matchedComponents.length) {
        return reject({code: 404})
      }
      Promise.all(matchedComponents.map(component => {
        if(component.asyncData) {
          return component.asyncData({
            store,
            route: router.currentRoute
          })
        }
      }))
      .then(() => {
        context.state = store.state
        resolve(app)
      })
      .catch(reject)
    }, reject)
  })
}

